import React, { useState } from 'react';
import api from '../../services/api';

const QuotaAdjustment: React.FC = () => {
  const [formData, setFormData] = useState({
    user_id: '',
    weekday_change: 0,
    weekend_change: 0,
    description: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess(false);
    setLoading(true);

    try {
      await api.post('/api/admin/adjust-quota', formData);
      setSuccess(true);
      setFormData({
        user_id: '',
        weekday_change: 0,
        weekend_change: 0,
        description: '',
      });
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Adjustment failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="card">
      <h2>Manual Quota Adjustment (ADM-04)</h2>
      {error && <div className="error">{error}</div>}
      {success && <div className="success">Quota adjusted successfully</div>}
      <form onSubmit={handleSubmit}>
        <label>
          User ID:
          <input
            type="number"
            value={formData.user_id}
            onChange={(e) => setFormData({ ...formData, user_id: e.target.value })}
            required
            className="input"
          />
        </label>
        <label>
          Weekday Change (positive to add, negative to subtract):
          <input
            type="number"
            value={formData.weekday_change}
            onChange={(e) => setFormData({ ...formData, weekday_change: parseInt(e.target.value) })}
            required
            className="input"
          />
        </label>
        <label>
          Weekend Change (positive to add, negative to subtract):
          <input
            type="number"
            value={formData.weekend_change}
            onChange={(e) => setFormData({ ...formData, weekend_change: parseInt(e.target.value) })}
            required
            className="input"
          />
        </label>
        <label>
          Description:
          <textarea
            value={formData.description}
            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
            className="input"
            rows={3}
          />
        </label>
        <button type="submit" className="btn btn-primary" disabled={loading}>
          {loading ? 'Adjusting...' : 'Adjust Quota'}
        </button>
      </form>
    </div>
  );
};

export default QuotaAdjustment;
