import React, { useEffect, useState } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import api from '../../services/api';

interface Cottage {
  id: number;
  cottage_id: string;
}

interface DateAvailability {
  date: string;
  is_available: boolean;
  is_booked: boolean;
  is_maintenance: boolean;
  is_holiday: boolean;
  is_peak_season: boolean;
  cost_weekday: boolean;
}

interface AvailabilityData {
  cottage_id: number;
  cottage_name: string;
  availability: DateAvailability[];
}

const BookingCalendar: React.FC = () => {
  const { user } = useAuth();
  const [cottages, setCottages] = useState<Cottage[]>([]);
  const [selectedCottage, setSelectedCottage] = useState<number | null>(null);
  const [availability, setAvailability] = useState<AvailabilityData | null>(null);
  const [checkIn, setCheckIn] = useState('');
  const [checkOut, setCheckOut] = useState('');
  const [costBreakdown, setCostBreakdown] = useState<any>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    fetchCottages();
  }, []);

  useEffect(() => {
    if (selectedCottage && checkIn && checkOut) {
      fetchAvailability();
      calculateCost();
    }
  }, [selectedCottage, checkIn, checkOut]);

  const fetchCottages = async () => {
    try {
      const response = await api.get('/api/owner/dashboard');
      setCottages(response.data.cottages);
    } catch (error) {
      console.error('Error fetching cottages:', error);
    }
  };

  const fetchAvailability = async () => {
    if (!selectedCottage || !checkIn || !checkOut) return;

    try {
      const response = await api.get(
        `/api/owner/availability/${selectedCottage}?start_date=${checkIn}&end_date=${checkOut}`
      );
      setAvailability(response.data);
    } catch (error) {
      console.error('Error fetching availability:', error);
    }
  };

  const calculateCost = async () => {
    if (!selectedCottage || !checkIn || !checkOut) return;

    try {
      const response = await api.post('/api/owner/calculate-cost', {
        cottage_id: selectedCottage,
        check_in: checkIn,
        check_out: checkOut,
      });
      setCostBreakdown(response.data);
    } catch (error) {
      console.error('Error calculating cost:', error);
    }
  };

  const handleBooking = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedCottage || !checkIn || !checkOut) {
      setError('Please select cottage and dates');
      return;
    }

    setError('');
    setSuccess(false);
    setLoading(true);

    try {
      await api.post('/api/owner/bookings', {
        cottage_id: selectedCottage,
        check_in: checkIn,
        check_out: checkOut,
      });
      setSuccess(true);
      setCheckIn('');
      setCheckOut('');
      setSelectedCottage(null);
      setCostBreakdown(null);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Booking failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <div className="card">
        <h2>Book a Cottage (OWN-04, OWN-05, OWN-06, OWN-07)</h2>
        {error && <div className="error">{error}</div>}
        {success && <div className="success">Booking request submitted successfully!</div>}

        <form onSubmit={handleBooking}>
          <label>
            Select Cottage:
            <select
              value={selectedCottage || ''}
              onChange={(e) => setSelectedCottage(parseInt(e.target.value))}
              required
              className="input"
            >
              <option value="">Select Cottage</option>
              {cottages.map((cottage) => (
                <option key={cottage.id} value={cottage.id}>
                  {cottage.cottage_id}
                </option>
              ))}
            </select>
          </label>

          <label>
            Check-in Date:
            <input
              type="date"
              value={checkIn}
              onChange={(e) => setCheckIn(e.target.value)}
              required
              className="input"
            />
          </label>

          <label>
            Check-out Date:
            <input
              type="date"
              value={checkOut}
              onChange={(e) => setCheckOut(e.target.value)}
              required
              className="input"
              min={checkIn}
            />
          </label>

          {costBreakdown && (
            <div style={{ marginTop: '20px', padding: '15px', backgroundColor: '#f8f9fa', borderRadius: '4px' }}>
              <h4>Cost Breakdown (OWN-06)</h4>
              <p><strong>Weekday Credits:</strong> {costBreakdown.weekday_credits}</p>
              <p><strong>Weekend Credits:</strong> {costBreakdown.weekend_credits}</p>
              <p><strong>Total Credits:</strong> {costBreakdown.total_credits}</p>
            </div>
          )}

          {availability && (
            <div style={{ marginTop: '20px' }}>
              <h4>Availability Calendar</h4>
              <div style={{ display: 'flex', flexWrap: 'wrap', gap: '5px' }}>
                {availability.availability.map((day) => (
                  <div
                    key={day.date}
                    style={{
                      padding: '5px',
                      width: '40px',
                      textAlign: 'center',
                      backgroundColor: day.is_available
                        ? '#d4edda'
                        : day.is_booked
                        ? '#f8d7da'
                        : '#fff3cd',
                      border: '1px solid #ddd',
                      borderRadius: '4px',
                      fontSize: '12px',
                    }}
                    title={
                      day.is_holiday
                        ? 'Holiday'
                        : day.is_peak_season
                        ? 'Peak Season'
                        : day.is_maintenance
                        ? 'Maintenance'
                        : day.is_booked
                        ? 'Booked'
                        : 'Available'
                    }
                  >
                    {new Date(day.date).getDate()}
                    {day.is_holiday && ' ðŸŸ¡'}
                  </div>
                ))}
              </div>
              <p style={{ fontSize: '12px', marginTop: '10px' }}>
                ðŸŸ¡ = Holiday/Peak Season (Weekend pricing)
              </p>
            </div>
          )}

          <button type="submit" className="btn btn-primary" disabled={loading || !costBreakdown}>
            {loading ? 'Submitting...' : 'Submit Booking Request'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default BookingCalendar;
