import React, { useEffect, useState } from 'react';
import api from '../../services/api';

interface QuotaStatus {
  weekday_quota: number;
  weekend_quota: number;
  weekday_balance: number;
  weekend_balance: number;
  available_weekday: number;
  available_weekend: number;
  pending_weekday: number;
  pending_weekend: number;
  escrowed_in_pending: {
    weekday: number;
    weekend: number;
  };
}

const QuotaStatus: React.FC = () => {
  const [quotaStatus, setQuotaStatus] = useState<QuotaStatus | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchQuotaStatus();
  }, []);

  const fetchQuotaStatus = async () => {
    try {
      const response = await api.get('/api/owner/quota-status');
      setQuotaStatus(response.data);
    } catch (error) {
      console.error('Error fetching quota status:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (!quotaStatus) return <div>Error loading quota status</div>;

  return (
    <div className="card">
      <h2>Quota Status (OWN-08, OWN-09)</h2>

      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginTop: '20px' }}>
        <div style={{ padding: '20px', backgroundColor: '#f8f9fa', borderRadius: '8px' }}>
          <h3>Weekday Credits</h3>
          <p><strong>Annual Quota:</strong> {quotaStatus.weekday_quota}</p>
          <p><strong>Current Balance:</strong> {quotaStatus.weekday_balance}</p>
          <p><strong>Available (Not Escrowed):</strong> {quotaStatus.available_weekday}</p>
          <p><strong>Escrowed in Pending:</strong> {quotaStatus.pending_weekday}</p>
        </div>

        <div style={{ padding: '20px', backgroundColor: '#f8f9fa', borderRadius: '8px' }}>
          <h3>Weekend Credits</h3>
          <p><strong>Annual Quota:</strong> {quotaStatus.weekend_quota}</p>
          <p><strong>Current Balance:</strong> {quotaStatus.weekend_balance}</p>
          <p><strong>Available (Not Escrowed):</strong> {quotaStatus.available_weekend}</p>
          <p><strong>Escrowed in Pending:</strong> {quotaStatus.pending_weekend}</p>
        </div>
      </div>

      <div style={{ marginTop: '20px', padding: '15px', backgroundColor: '#e7f3ff', borderRadius: '4px' }}>
        <p><strong>Note:</strong> Credits shown as "Escrowed" are locked in pending booking requests and will be refunded if the request is rejected or cancelled.</p>
      </div>
    </div>
  );
};

export default QuotaStatus;
