import React, { useState, useEffect } from 'react';
import api from '../../services/api';

interface PeakSeason {
  id: number;
  name: string;
  start_date: string;
  end_date: string;
}

const PeakSeasonManagement: React.FC = () => {
  const [formData, setFormData] = useState({
    name: '',
    start_date: '',
    end_date: '',
  });
  const [savedSeasons, setSavedSeasons] = useState<PeakSeason[]>([]);
  const [loading, setLoading] = useState(false);
  const [fetching, setFetching] = useState(true);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchSavedSeasons();
  }, []);

  const fetchSavedSeasons = async () => {
    try {
      const response = await api.get('/api/admin/peak-seasons');
      setSavedSeasons(response.data);
    } catch (error) {
      console.error('Error fetching peak seasons:', error);
    } finally {
      setFetching(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess(false);
    setLoading(true);

    try {
      await api.post('/api/admin/peak-seasons', formData);
      setSuccess(true);
      setFormData({ name: '', start_date: '', end_date: '' });
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to create peak season');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="card">
      <h2>Peak Season Definition (ADM-15)</h2>
      {error && <div className="error">{error}</div>}
      {success && <div className="success">Peak season created successfully</div>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Peak Season Name (e.g., Summer Peak)"
          value={formData.name}
          onChange={(e) => setFormData({ ...formData, name: e.target.value })}
          required
          className="input"
        />
        <input
          type="date"
          placeholder="Start Date"
          value={formData.start_date}
          onChange={(e) => setFormData({ ...formData, start_date: e.target.value })}
          required
          className="input"
        />
        <input
          type="date"
          placeholder="End Date"
          value={formData.end_date}
          onChange={(e) => setFormData({ ...formData, end_date: e.target.value })}
          required
          className="input"
        />
        <button 
          type="submit" 
          className="btn btn-primary" 
          disabled={loading}
          title={loading ? 'Creating...' : 'Create Peak Season'}
          style={{ padding: '5px 10px', minWidth: 'auto' }}
        >
          {loading ? '⏳' : '➕'}
        </button>
      </form>
    </div>
  );
};

export default PeakSeasonManagement;
