import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../services/api';

interface User {
  id: number;
  name: string;
  email: string;
  phone: string;
  status: string;
  weekday_balance: number;
  weekend_balance: number;
}

interface Booking {
  id: number;
  check_in: string;
  check_out: string;
  status: string;
}

const MemberLookup: React.FC = () => {
  const navigate = useNavigate();
  const [query, setQuery] = useState('');
  const [users, setUsers] = useState<User[]>([]);
  const [selectedUser, setSelectedUser] = useState<any>(null);
  const [loading, setLoading] = useState(false);
  const [actionLoading, setActionLoading] = useState(false);

  const handleSearch = async () => {
    if (!query.trim()) return;
    setLoading(true);
    try {
      const response = await api.get(`/api/admin/search-members?query=${query}`);
      setUsers(response.data);
    } catch (error) {
      console.error('Error searching members:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleViewDetails = async (userId: number) => {
    try {
      const response = await api.get(`/api/admin/member/${userId}`);
      setSelectedUser(response.data);
    } catch (error) {
      console.error('Error fetching member details:', error);
    }
  };

  const handleDeactivate = async (userId: number, userName: string) => {
    if (!window.confirm(`Are you sure you want to deactivate ${userName}? This will prevent them from making new bookings.`)) {
      return;
    }

    setActionLoading(true);
    try {
      await api.post(`/api/admin/deactivate-member/${userId}`);
      alert(`${userName} has been deactivated successfully.`);
      // Refresh the search results
      if (query.trim()) {
        handleSearch();
      }
      // Clear selected user if it's the deactivated one
      if (selectedUser && selectedUser.user.id === userId) {
        setSelectedUser(null);
      }
    } catch (error: any) {
      alert(error.response?.data?.detail || 'Failed to deactivate member');
    } finally {
      setActionLoading(false);
    }
  };

  const handleDelete = async (userId: number, userName: string) => {
    const confirmMessage = `⚠️ WARNING: This will permanently delete ${userName} and ALL their related records including:\n\n` +
      `- All bookings\n` +
      `- All quota transactions\n` +
      `- User account\n\n` +
      `This action CANNOT be undone!\n\n` +
      `Are you absolutely sure you want to delete this user?`;

    if (!window.confirm(confirmMessage)) {
      return;
    }

    // Double confirmation
    if (!window.confirm(`Final confirmation: Delete ${userName} permanently?`)) {
      return;
    }

    setActionLoading(true);
    try {
      await api.delete(`/api/admin/member/${userId}`);
      alert(`${userName} and all related records have been deleted successfully.`);
      // Refresh the search results
      if (query.trim()) {
        handleSearch();
      }
      // Clear selected user if it's the deleted one
      if (selectedUser && selectedUser.user.id === userId) {
        setSelectedUser(null);
      }
    } catch (error: any) {
      alert(error.response?.data?.detail || 'Failed to delete member');
    } finally {
      setActionLoading(false);
    }
  };

  return (
    <div className="card">
      <h2>Member Lookup & History (ADM-03)</h2>
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search by name, email, or phone"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="input"
          style={{ width: '300px', display: 'inline-block', marginRight: '10px' }}
        />
        <button onClick={handleSearch} className="btn btn-primary" disabled={loading}>
          Search
        </button>
      </div>

      {users.length > 0 && (
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Phone</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.id}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.phone}</td>
                <td>
                  <span className={`badge badge-${user.status}`}>{user.status}</span>
                </td>
                <td>
                  <button onClick={() => handleViewDetails(user.id)} className="btn btn-secondary" style={{marginRight: '5px'}}>
                    View Details
                  </button>
                  <button onClick={() => navigate(`/admin/edit-member/${user.id}`)} className="btn btn-primary">
                    Edit
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {selectedUser && (
        <div className="card" style={{ marginTop: '20px' }}>
          <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px'}}>
            <h3>Member Details</h3>
            <button 
              onClick={() => navigate(`/admin/edit-member/${selectedUser.user.id}`)} 
              className="btn btn-primary"
            >
              Edit Credentials
            </button>
          </div>
          <p><strong>Name:</strong> {selectedUser.user.name}</p>
          <p><strong>Email:</strong> {selectedUser.user.email}</p>
          <p><strong>Phone:</strong> {selectedUser.user.phone}</p>
          <p><strong>Weekday Balance:</strong> {selectedUser.user.weekday_balance}</p>
          <p><strong>Weekend Balance:</strong> {selectedUser.user.weekend_balance}</p>
          <h4>Booking History</h4>
          <table className="table">
            <thead>
              <tr>
                <th>Check-in</th>
                <th>Check-out</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {selectedUser.bookings.map((booking: Booking) => (
                <tr key={booking.id}>
                  <td>{new Date(booking.check_in).toLocaleDateString()}</td>
                  <td>{new Date(booking.check_out).toLocaleDateString()}</td>
                  <td>
                    <span className={`badge badge-${booking.status}`}>{booking.status}</span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default MemberLookup;
